import React, { Component } from "react";
import './Track.css'

class Track extends Component {
	
	constructor(props) {
		super();
		this.state = {
			isHidden: true,
			songData: props.item.data,
			songName: props.item.name
		}
		
		if (this.state.songData.title != undefined) {
			this.state.songName = this.state.song.data.title;
		}
	}
	
	expand(item) {
		this.setState({
			isHidden: false
		});
	}
	
	hide(item) {
		this.setState({
			isHidden: true
		});
	}
	
	render () {
		return (
			<div className={this.state.isHidden ? "shortened" : "expanded"} style={{position: "relative", border: "solid 1px black", left: "5%", top: "50px" }} onMouseLeave={(e, val) => { this.hide(this); }} onMouseEnter={(e, val) => { this.expand(this); }}>
				{this.state.isHidden && <Hidden data={this.state.songData} name={this.state.songName}/>}
				{!this.state.isHidden && <Expanded data={this.state.songData} name={this.state.songName}/>}
			</div>
		)
	}
}

const Hidden = (props) => (
	<div className="overflow">{ props.name }</div>
)

const Expanded = (props) => (
	console.log(props),
	<div>{ props.name }</div>
)

export default Track;
