import React, { Component } from "react";
import { Slider } from '@material-ui/core';

class PlayBackSlider extends Component {

    constructor(props) {
      super();
      this.state = {
        currentPos: 0
      }
      
      var locked = true;
    }
  
    onPlayBackStateChange(val) {
      this.locked = false;
      console.log(this.locked + " " + this.props.play);


    }
    
    onPlayBackStateChangeCommitted(val) {
      this.locked = true;
      this.setState({ currentPos: val }, () => { this.props.client.send(JSON.stringify(this.state)); console.log(this.state.currentPos); })
    }
  
  render() {
    const sliderStyle = {
      'marginLeft': '25%',
      'marginRight': '25%',
    };
    var value = (this.props.play && this.locked) ? this.props.currentPos : this.state.currentPos;
    return (
    <div className="PlayBackSlider">
      <div style={sliderStyle}>
        <Slider id="slider" 
          onChange={(e, val) => {
            this.onPlayBackStateChange(val);
          } }
          onChangeCommitted={(e, val) => {
            this.onPlayBackStateChangeCommitted(val);
          } }
          min={0}
          max={this.props.duration}
          value={value}
        />
      </div>
    </div>
    );
  }
}

export default PlayBackSlider;
