import React, { Component } from "react";
import './Track.css'

class Track extends Component {
	
	constructor(props) {
		super();
		console.log(props);
		this.state = {
			isHidden: true,
			song: props.item
		}
	}
	
	
	
	expand(item) {
		this.setState({
			isHidden: !this.state.isHidden
		});
	}
	
	render () {
		if (this.state.isHidden) {
			return (
				<div className="track-block shortened" style={{position: "relative", border: "solid 1px black", left: "5%", top: "50px" }} onMouseOver={(e, val) => { this.expand(this); }}>
					<Hidden song={this.state.song}/>
				</div>
			)
		}
		else {
			return (
				<div className="track-block" style={{position: "relative", border: "solid 1px black", left: "5%", top: "50px" }} onMouseOver={(e, val) => { this.expand(this); }}>
					<Expanded song={this.state.song}/>
				</div>	
			);
		}
	}
}

const Hidden = (props) => (
	console.log(props),
	<div className="shortened">{ props.song }</div>
)

const Expanded = (props) => (
	<div className="expanded">{ props.song }</div>
)

export default Track;
