import React, { Component } from "react";
import './Track.css'

class Track extends Component {
	
	constructor(props) {
		super();
		this.state = {
			isHidden: true,
			song: props.item,
			songName: ""
		}
		
		if (this.state.song.data.title != undefined) {
			this.setState({
				songName: this.state.song.data.title
			});
		}
		else {
			this.setState({
				songName: this.state.song.name
			});
		}
	}
	
	expand(item) {
		this.setState({
			isHidden: false
		});
	}
	
	hide(item) {
		this.setState({
			isHidden: true
		});
	}
	
	render () {
		return (
			<div className={this.state.isHidden ? "shortened" : "expanded"} style={{position: "relative", border: "solid 1px black", left: "5%", top: "50px" }} onMouseLeave={(e, val) => { this.hide(this); }} onMouseEnter={(e, val) => { this.expand(this); }}>
				{this.state.isHidden && <Hidden song={this.state}/>}
				{!this.state.isHidden && <Expanded song={this.state}/>}
			</div>
		)
	}
}

const Hidden = (props) => (
	<div className="overflow">{ props.songName }</div>
)

const Expanded = (props) => (
	console.log(props.song),
	<div>{ props.songName }</div>
)

export default Track;
