import React, { Component } from "react";
import './Track.css'

class Track extends Component {
	
	constructor(props) {
		super();
		console.log(props);
		this.state = {
			isHidden: true,
			song: props.item
		}
	}
	
	
	
	expand(item) {
		this.setState({
			isHidden: !this.state.isHidden
		});
		console.log(item);
	}
	
	render () {
		return (
			<div className="track-block" style={{position: "relative", border: "solid 1px black", left: "5%", top: "50px" }} onClick={(e, val) => { this.expand(this); }}>
				{this.state.isHidden && <Hidden song={this.state.song}/>}
				{!this.state.isHidden && <Expanded className="expanded" song={this.state.song}/>}
			</div>
		);
	}
}

const Hidden = (props) => (
	console.log(props),
	<div>{ props.song }</div>
)

const Expanded = (props) => (
	<div>{ props.song }</div>
)

export default Track;
