import React, { Component } from "react";
import './Track.css'

class Track extends Component {
	
	constructor(props) {
		super();
		this.state = {
			isHidden: true
		}
	}
	
	expand(item) {
		this.setState({
			isHidden: !this.state.isHidden
		});
		console.log(item);
	}
	
	render () {
		return (
			<div className="track-block" style={{position: "relative", border: "solid 1px black", width: "10%", left: "5%", top: "50px" }} onClick={(e, val) => { this.expand(this); }}>
				{this.state.isHidden && <Hidden />}
				{!this.state.isHidden && <Expanded />}
			</div>
		);
	}
}

const Hidden = () => (
	<div>test</div>
)

const Expanded = () => (
	<div>other test</div>
)

export default Track;
