import React, { Component } from "react";
import './Track.css'

class Track extends Component {
	
	constructor(props) {
		super();
		console.log(props);
		this.state = {
			isHidden: true,
			song: props.item
		}
	}
	
	
	
	expand(item) {
		this.setState({
			isHidden: false
		});
	}
	
	hide(item) {
		this.setState({
			isHidden: true
		});
	}
	
	render () {
		return (
			<div className={this.state.isHidden ? "shortened" : "expanded"} style={{position: "relative", border: "solid 1px black", left: "5%", top: "50px" }} onMouseLeave={(e, val) => { this.hide(this); }} onMouseEnter={(e, val) => { this.expand(this); }}>
				{this.state.isHidden && <Hidden song={this.state.song}/>}
				{!this.state.isHidden && <Expanded song={this.state.song}/>}
			</div>
		)
	}
